// 创建路由
import VueRouter from 'vue-router'
// 引入组件
import Login from '../views/admin/Login'
import Index from '../views/admin/Index'
import User from '../views/admin/user/User'
import Tagg from '../views/admin/tag/Tagg'
import Channel from '../views/admin/channel/Channel'
import ChannelAdd from '../views/admin/channel/Add'
import ChannelEdit from '../views/admin/channel/Edit'
import FriendLink from '../views/admin/friendlink/FriendLink'
import FriendLinkAdd from '../views/admin/friendlink/Add'
import FriendLinkEdit from '../views/admin/friendlink/Edit'
import Article from '../views/admin/article/Article'
import ArticleAdd from '../views/admin/article/Add'
import ArticleEdit from '../views/admin/article/Edit'
import Comment from '../views/admin/comment/Comment'
import Info from '../views/admin/user/Info'
import token from '../store/token'

import FIndex from '../views/front/FIndex'

const router = new VueRouter({
  routes: [
    {
      path: '/',
      name: "fIndex",
      component: FIndex,
      meta: {
        requireLogin: false
      }
    },
    {
      path: '/login',
      name: 'login',
      component: Login
    },
    {
      path: '/index',
      name: 'index',
      component: Index,
      children: [
        {
          path: '/info',
          name: 'info',
          component: Info
        },
        {
          path: '/user',
          name: 'user',
          component: User
        }, {
          path: '/channel',
          name: 'channel',
          component: Channel
        }, {
          path: '/channel_add',
          name: 'channel_add',
          component: ChannelAdd
        }, {
          path: '/channel_edit',
          name: 'channel_edit',
          component: ChannelEdit
        }, {
          path: '/tag',
          name: 'tag',
          component: Tagg
        }, {
          path: '/article',
          name: 'article',
          component: Article
        }, {
          path: '/article_add',
          name: 'article_add',
          component: ArticleAdd
        }, {
          path: '/article_edit',
          name: 'article_edit',
          component: ArticleEdit
        }, {
          path: '/comment',
          name: 'comment',
          component: Comment
        }, {
          path: '/friend',
          name: 'friend',
          component: FriendLink
        }, {
          path: '/friend_add',
          name: 'friend_add',
          component: FriendLinkAdd
        }, {
          path: '/friend_edit',
          name: 'friend_edit',
          component: FriendLinkEdit
        }
      ]
    },

  ]
})

router.beforeEach((to, from, next) => {

  if (!to.matched.some(r => r.meta.requireLogin)) {
    next();
  } else {
    if (token.get()) {
      next()
    } else {
      if (to.path === '/login') {
        next()
      } else {
        next('/login')
      }
    }

  }

})

export default router
